mapper.select:
with dvp_cst_req as (
select #{item.ordCstNo, jdbcType=VARCHAR} as ord_cst_no, #{item.dvPlcTpCd, jdbcType=VARCHAR} as dv_plc_tp_cd, #{item.imtnRsnCcd, jdbcType=VARCHAR} as imtn_rsn_ccd
) select dvp_cst_req.*, cst.dv_grp_no, cst.aply_ccd, cst.org_ord_cst_no, cst.clm_no, cst.ord_no, cst.dv_amt_tp_cd, cst.org_dv_amt, cst.dv_bnf_amt, cst.aply_dv_amt, cst.cncl_dv_amt from op_ord_cost_info cst inner join dvp_cst_req on cst.ord_cst_no = dvp_cst_req.ord_cst_no

select *
from op_clm_info clm
where

        (
            clm.ord_no = #{item.ordNo}
            and clm.ord_seq =  #{item.ordSeq}
            and clm.org_proc_seq = #{item.procSeq}
        )
