mapper.insert:



insert into op_pay_info(
                        pay_no
                        , ord_no
                        , clm_no
                        , pay_mn_cd
                        , pay_ccd
                        , pay_prgs_scd
                        , pay_amt
                        , cncl_amt
                        , rfnd_avl_amt
                        , trsn_id
                        , pay_cmt_dtime
                        , sys_reg_dtime
                        , sys_regr_id
                        , sys_mod_dtime
                        , sys_modr_id
                        , org_pay_no
                        , vr_acct
                        , vr_acct_nm
                        , vr_bnk_cd
                        , vr_val_dt
                        , vr_val_tt)
        values(
               #{payNo}
               , #{ordNo}
               , #{clmNo}
               , #{payMnCd}
               , #{payCcd}
               , #{payPrgsScd}
               , #{payAmt}
               , #{cnclAmt}
               , #{rfndAvlAmt}
               , #{trsnId}
               , #{payCmtDtime}
               , now()
               , #{loginId}
               , now()
               , #{loginId}
               , #{orgPayNo}
               , #{vrAcct}
               , #{vrAcctNm}
               , #{vrBnkCd}
               , #{vrValDt}
               , #{vrValTt}
               )

mapper.update:
update op_pay_info
set
    pay_prgs_scd= #{payPrgsScd},
    cncl_amt=#{cnclAmt}, 
    rfnd_avl_amt=#{rfndAvlAmt}, 
    pay_cmt_dtime=#{payCmtDtime}
    sys_mod_dtime = now(),
    sys_modr_id = #{loginId}
where vr_acct= #{vrAcct}
