mapper.sql:
, prd as(
select #{productInfoVO.goodsNo, jdbcType=VARCHAR} as goods_no, #{productInfoVO.itemNo, jdbcType=VARCHAR} as item_no, #{productInfoVO.orrCnt, jdbcType=INTEGER} as orr_cnt, #{productInfoVO.orrAt, jdbcType=INTEGER} as orr_at, #{productInfoVO.salePrc, jdbcType=INTEGER} as sale_prc, #{productInfoVO.prmPrc, jdbcType=INTEGER} as prm_prc, #{productInfoVO.appliedCpnIssNo, jdbcType=INTEGER} as applied_cpn_iss_no
), aply as ( select prd.*, apy.prm_no from cc_prm_aply_tgt apy inner join prd on apy.aply_tgt_no = prd.goods_no and apy.aply_tgt_ccd = '10' )
