mapper.sql:
, prd as(
select #{productInfoVO.goodsNo, jdbcType=VARCHAR} as goods_no, #{productInfoVO.itemNo, jdbcType=VARCHAR} as item_no, #{productInfoVO.orrCnt, jdbcType=INTEGER} as orr_cnt, #{productInfoVO.orrAt, jdbcType=INTEGER} as orr_at, #{productInfoVO.salePrc, jdbcType=INTEGER} as sale_prc, #{productInfoVO.prmPrc, jdbcType=INTEGER} as prm_prc
), aply as ( select prd.*, apy.prm_no from cc_prm_aply_tgt apy inner join prd on apy.aply_tgt_no = prd.goods_no )

select aply.goods_no, aply.item_no, aply.orr_cnt, aply.orr_at, aply.sale_prc, aply.prm_prc, ci.prm_no, ci.prm_nm, ci.dc_ccd, ci.dc_val, ci.min_pur_amt, ci.max_dc_amt, ci.cpn_kind_cd, ci.degr_ccd, iss.cpn_iss_no, case when ci.prm_priod_ccd = '10' or ci.prm_kind_cd = '10' then ci.prm_end_dt else iss.sys_mod_dtime + ci.prm_std_dd * interval '1 day' end prm_end_dt from cup_info ci inner join aply on ci.prm_no = aply.prm_no left join cc_cpn_issue iss on aply.prm_no = iss.prm_no and iss.mbr_no = #{mbrNo} where 1 = 1 and now() < ci.prm_end_dt and ci.prm_kind_cd = #{prmKindCd} and ci.cpn_kind_cd = #{cpnKindCd}
and iss.cpn_iss_no is not null and iss.cpn_use_dt is null
and ci.min_pur_amt <= coalesce(aply.orr_at, coalesce(aply.sale_prc, 0))
