mapper.sql:
, prd as(
select #{productInfoVO.goodsNo} as goods_no, #{productInfoVO.itemNo} as item_no, #{productInfoVO.orrCnt} as orr_cnt, #{productInfoVO.orrAt} as orr_at, #{productInfoVO.salePrc} as sale_prc, #{productInfoVO.prmPrc} as prm_prc
), aply as ( select prd.*, apy.prm_no from cc_prm_aply_tgt apy inner join prd on apy.aply_tgt_no = prd.goods_no )
